mapper.select:
SELECT RN, ord_code, mbsp_id, ord_name, ord_price, pay_method, ord_regdate FROM ( select /*+INDEX_DESC(order_tbl PK_ORD_CODE) */ rownum RN, ord_code, mbsp_id, ord_name, ord_price, pay_method, ord_regdate from order_tbl where
ROWNUM <= (#{cri.pageNum} * #{cri.amount})
) WHERE RN > ((#{cri.pageNum} - 1) * #{cri.amount})

mapper.delete:

delete from
	ordetail_tbl
where
	ord_code = #{ord_code}
and
	pro_num = #{pro_num}
